import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Curd from "./Curd";

const Edit = () => {
  const { id } = useParams();
  const [student, setStudent] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAlldata();
    edit();
  }, []);

  const getAlldata = async () => {
    try {
      const student = await axios
        .get(`http://localhost:4500/data/${id}`)
        .then((res) => {
          console.log(res.data);
          setStudent(student.data);
        });
    } catch (err) {
      console.log(err, "data view time error");
    }
  };
  const changetext = async (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };
  const edit = async (e) => {
    e.preventDefault();
    try {
      await axios
        .put(`http://localhost:4500/data/${id}`, student)
        .then((res) => {
          console.log(res.data);
          navigate("/", { replace: true });
          setStudent(res.data);
        });
    } catch (err) {
      console.log(err, "error in edit time");
    }
  };

  return (
    <>
      <div className="container">
        <div className="col-md-8 offset-2  text-center p-5 d-grid">
          <h3>Edit item</h3>

          <input
            type="text"
            className="p-1 mb-2"
            value={student.name}
            placeholder="New Name Add"
            onChange={(e) => changetext(e)}
            name="name"
          />

          <input
            type="id"
            value={student.id}
            className="p-1 mb-2"
            placeholder="New Email Add"
            onChange={(e) => changetext(e)}
            name="id"
          />

          <button className="btn btn-warning" onClick={(e) => edit(e)}>
            Edit
          </button>
        </div>
      </div>
    </>
  );
};

export default Edit;
