import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Add = () => {
  const [student, setStudent] = useState([]);
  const [status, setStatus] = useState();
  const navigate = useNavigate();

  const textchange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const addItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:4500/data", student).then((res) => {
        console.log(res, "resruiuyuy");
        navigate("/", { replace: true });
        setStatus(true);
      });
    } catch (err) {
      console.log(err, "data post time error");
    }
  };
  return (
    <>
      <div className="container ">
        <div className="col-md-6 text-center p-2  d-grid">
          <h3>Add item</h3>

          <input
            type="text"
            className="p-1 mb-2"
            placeholder="New Name Add"
            onChange={(e) => textchange(e)}
            name="name"
          />

          <input
            type="number"
            className="p-1 mb-2"
            placeholder="New Email Add"
            onChange={(e) => textchange(e)}
            name="id"
          />

          <button className="btn btn-warning" onClick={(e) => addItem(e)}>
            ADD
          </button>
        </div>
      </div>
    </>
  );
};

export default Add;
