import React, { useState } from "react";
// import axios from "axios";
import List from "./List";
import axios from "axios";

const Curd = () => {
  const [student, setStudent] = useState([]);
  const [status, setStatus] = useState();

  const textchange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const addItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post("ttp://localhost:4500/data", student);
      // console.log(res, "resruiuyuy");
      setStatus(true);
    } catch (err) {
      console.log(err, "data post time error");
    }
  };
  if (status) {
    return (
      <>
        <Curd />
      </>
    );
  }

  return (
    <>

      <div className="Main_div">
        <div className="container">
          <div className="row">
            <div className="col-md-12 text-center">
              <h2 style={{ backgroundColor: "black", color: "white" }}>
                Curd Operation
              </h2>
            </div>

            <div className="col-md-6 text-center p-2  d-grid">
              <h3>Add item</h3>

              <input
                type="text"
                className="p-1 mb-2"
                placeholder="New Name Add"
                onChange={(e) => textchange(e)}
                name="name"
              />

              <input
                type="number"
                className="p-1 mb-2"
                placeholder="New Email Add"
                onChange={(e) => textchange(e)}
                name="id"
              />

              <button className="btn btn-warning" onClick={(e) => addItem(e)}>
                ADD
              </button>
            </div>
            <List />
          </div>
        </div>
      </div>
    </>
  );
};

export default Curd;
