import { ActionType } from "../contanst/action-type";

const initialState = {
  data: [],
};

export const dataReducer = (
  state = initialState,

  { type, payload }
) => {
  switch (type) {
    case ActionType.GET_DATA:
      return { ...state, data: payload };
    default:
      return state;
  }
};
