import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const List = () => {
  const [data, setData] = useState([]);
  const [status, setStatus] = useState();

  useEffect(() => {
    getAlldata();
    handleDelete();
  }, []);

  const getAlldata = async () => {
    try {
      const all = await axios.get("http://localhost:4500/data").then((res) => {
        setData(res.data);
      });
    } catch (err) {
      console.log(err, "data get time error");
    }
  };
  const handleDelete = (id) => {
    // console.log(id, "jashdjkahskdjhaskjh");
    try {
      axios.delete(`http://localhost:4500/data/${id}`).then((res) => {
        if (id !== res.id) {
          setStatus(true);
        }
        // setData(id);
      });
    } catch (err) {
      console.log(err, "delete error");
    }
  };
  if (status) {
    return (
      <>
        <List />
      </>
    );
  }
  return (
    <>
      <div className="mt-5 text-center">
        <h3>List Of Item</h3>
        <table className="col-md-12 mt-5">
          <tr className="mt-5">
            <td>Name</td>
            <td>I'D</td>
            <td>Action</td>
          </tr>

          {data.map((ele, i) => {
            return (
              <>
                <tr>
                  <td>{ele.name}</td>
                  <td>{ele.id}</td>
                  <td className="Icons text-center  d-flex">
                    <button className="btn-2">
                      <Link to={`/view/${ele.id}`}>
                        <i className="icon1 fa-solid fa-eye"></i>
                      </Link>
                    </button>
                    <Link to={`/edit/${ele.id}`}>
                      <i
                        style={{ color: "orange" }}
                        className="p-3 fa-solid fa-ellipsis-vertical"
                      ></i>
                    </Link>
                    <button
                      className="btn-2"
                      onClick={() => handleDelete(ele.id)}
                    >
                      <i className="icon3 fa-solid fa-delete-left"></i>
                    </button>
                  </td>
                </tr>
              </>
            );
          })}
        </table>
      </div>
    </>
  );
};

export default List;
