import React from "react";
import { NavLink } from "react-router-dom";
import Dropdown from "react-bootstrap/Dropdown";

// import { Button, PageHeader } from "antd";
// import { useNavigate } from "react-router-dom";
// import Nav from "react-bootstrap/Nav";
// import { Link } from "react-router-dom";

const Header = () => {
  let activeStyle = {
    color: "orange",

    borderRadius: "5px",
    backgroundColor: "white",
  };
  let notActiveStyle = {
    color: "black",
  };

  return (
    <>
      <div className="Main-div">
        <div className="container">
          <div className="row">
            <nav className="nav-2 navbar navbar-expand-lg navbar-light">
              <NavLink
                className="navbar-brand nav-link"
                to="/"
                style={({ isActive }) => (isActive ? activeStyle : activeStyle)}
              >
                ToDo
              </NavLink>

              <div
                className="collapse navbar-collapse justify-content-end"
                id="navbarSupportedContent"
              >
                <NavLink
                  className=" nav-link"
                  to="/"
                  style={({ isActive }) =>
                    isActive ? activeStyle : notActiveStyle
                  }
                >
                  Home
                </NavLink>
                <NavLink
                  className=" nav-link"
                  to="/service"
                  style={({ isActive }) =>
                    isActive ? activeStyle : notActiveStyle
                  }
                >
                  Service
                </NavLink>
                <NavLink
                  className=" nav-link"
                  to="/contect"
                  style={({ isActive }) =>
                    isActive ? activeStyle : notActiveStyle
                  }
                >
                  Contect
                </NavLink>
                <ul className="col-6  navbar-nav me-auto justify-content-end mb-lg-0">
              <li className="icon-1 nav-item">
                <Dropdown className="dropd">
                  <Dropdown.Toggle
                    id="dropdown-button-dark-example1"
                    variant=""
                  >
                    <i class="Main-icon fa-solid fa-circle-user"></i>
                  </Dropdown.Toggle>

                  <Dropdown.Menu variant="dark">
                    <Dropdown.Item className="dropdown">
                      <NavLink
                        to={`/about`}
                        style={{ textDecoration: "none" }}
                        aria-current="page"
                      >
                        <span className="" style={{ color: "#DEE2E6" }}>
                          Profile
                        </span>
                      </NavLink>
                    </Dropdown.Item>
                    </Dropdown.Menu>
                    </Dropdown>
                    </li>
                    </ul>
              </div>
            </nav>
          </div>
        </div>
      </div>
    </>
  );
};

export default Header;
