import React, { useState, useEffect, Fragment } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const List = ({ handleDelete }) => {
  // const [status, setStatus] = useState();
  const [data, setData] = useState([]);

  useEffect(() => {
    getalldata();
  }, []);
  const getalldata = async () => {
    try {
      await axios
        .get("http://localhost:4500/data")
        .then((res) => {
          console.log(res.data);
          // setData(res.data);
        });
    } catch (err) {
      console.log(err, "list data not get");
    }
  };

  return (
    <>
      <tbody>
        {data.map((ele) => {
          return (
            <Fragment key={ele.id}>
              <tr>
                <td>{ele.id}</td>
                <td>{ele.name}</td>
                <td>{ele.completed}</td>
                <td className="Icons text-center  d-flex">

                  <button className="btn-2">
                    <Link to={`/edit/${ele.id}`}>
                      <i className="p-2 fa-solid fa-ellipsis-vertical"></i>
                    </Link>
                  </button>
                  <button
                    className="btn-2"
                    onClick={() => handleDelete(ele.id)}
                  >
                    <i className="icon3 fa-solid fa-delete-left"></i>
                  </button>
                </td>
              </tr>
            </Fragment>
          );
        })}
      </tbody>
    </>
  );
};

export default List;















































