import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import Footer from "./Footer";
import { useSelector, useDispatch } from "react-redux";
import {postDatas} from './'

const Add = () => {
  const seletectdata = useSelector((state) => state.data);
  const dispatch = useDispatch();

  const [student, setStudent] = useState([]);

  const navigate = useNavigate();

  const textchange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const addItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:4500/data", student).then((res) => {
        console.log(res, "resruiuyuy");
        navigate("/", { replace: true });
        dispatch(postDatas())
      });
    } catch (err) {
      console.log(err, "data post time error");
    }
  };
  return (
    <>
      <Header />
      <div className="Main_div ">
        <div className="container main-div-1 mt-5">
          <div className="row col-md-12">
            <div className="col-md-8 offset-2   text-center p-5 d-grid">
              <h3>Add item</h3>

              <input
                type="text"
                className="p-1 mb-2"
                placeholder="Name Add"
                onChange={(e) => textchange(e)}
                name="name"
              />

              <input
                type="number"
                className="p-1 mb-2"
                placeholder="Number Add"
                onChange={(e) => textchange(e)}
                name="id"
              />

              <button
                className="btn col-md-12 mt-5 btn-warning"
                onClick={(e) => addItem(e)}
              >
                ADD
              </button>
              <button
                className="btn btn-warning mt-2"
                onClick={(e) => navigate("/", { replace: true })}
              >
                Back
              </button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Add;
