import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import axios from "axios";
import List from "./List";

const Home = () => {
  const [item, setItem] = useState([]);
  // const [status, setStatus] = useState();

  useEffect(() => {
    getalldata();
  }, []);
  const getalldata = async () => {
    try {
      await axios.get("  http://localhost:4500/data").then((res) => {
        // console.log(res.data);
        setItem(res.data);
      });
    } catch (err) {
      console.log(err, "list data not get");
    }
  };

  const textChange = (e) => {
    setItem({ ...item, [e.target.name]: e.target.value });
  };
  const Additem = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:4500/data`, item).then((res) => {
        setStatus(true);
      });
    } catch (err) {
      console.log(err, "add item time error");
    }
  };
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4500/data${id}`).then((res) => {
        console.log(res, "delete");
        setStatus(true);
      });
    } catch (e) {
      console.log(e, "delete time error");
    }
  };

  if (status) {
    return <Home />;
  }

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-md-12 text-center p-3">
            <h1>Home Page</h1>
          </div>

          <div className="col-md-12 mt-5 text-center">
            <h1>Add Items</h1>
            <input
              type="text"
              placeholder="Item Name"
              className="mx-3 p-2"
              onChange={(e) => textChange(e)}
              name="name"
            />
            <input
              type="number"
              placeholder="Item Price"
              className="mx-3 p-2"
              onChange={(e) => textChange(e)}
              name="id"
            />
            <br />
            <br />
            <button className="btn btn-primary " onClick={(e) => Additem(e)}>
              Add
            </button>
          </div>

          <div className="col-md-12 text-center mt-5">
            <div className="col-md-12">
              <h2>List of Items</h2>
            </div>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Item</th>
                  <th>Complete</th>
                  <th>Action</th>
                </tr>
              </thead>
              <List handleDelete={handleDelete} />
            </Table>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;

