import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Home from "./Home";

const Edit = () => {
  const [item, setItem] = useState({
    title: "",
    completed: "",
  });
  const [status, setStatus] = useState();
  const { id } = useParams();
  const navigate = useNavigate();
  // const navigate = useNavigate();

  useEffect(() => {
    getalldata();
  }, []);
  const getalldata = async () => {
    try {
      const item = await axios.get(`http://localhost:4500/data${id}`);
      // console.log(res.data);
      setItem(item.data);
    } catch (err) {
      console.log(err, "list data not get");
    }
  };

  const textChange = (e) => {
    setItem({ ...item, [e.target.name]: e.target.value });
  };
  const Edititem = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4500/data${id}`, item).then((res) => {
        console.log(res);
        // setStudent(res.data);
        setStatus(true);
      });
    } catch (err) {
      console.log(err, "edit time error");
    }
  };
  if (status) {
    return <Home />;
  }
  return (
    <>
      <div className="col-md-12 mt-5 text-center">
        <h1>Edit Items</h1>
        <input
          type="text"
          placeholder="Item Name"
          className="mx-3 p-2 mt-5"
          value={item.title}
          onChange={(e) => textChange(e)}
          name="name"
        />
        <input
          type="number"
          placeholder="Item Price"
          className="mx-3 mt-5 p-2"
          value={item.completed}
          onChange={(e) => textChange(e)}
          name="price"
        />
        <br />
        <br />
        {/* onClick={() => navigate(`/edit/${item.id}`, { replace: true }) */}
        <button className="btn btn-primary mt-5" onClick={(e) => Edititem(e)}>
          Edit
        </button>
        <button
          className="btn btn-primary mx-3 mt-5"
          onClick={() => navigate("/", { replace: true })}
        >
          Back
        </button>
      </div>
    </>
  );
};

export default Edit;
