import { Button, PageHeader } from "antd";
import { useNavigate } from "react-router-dom";
import react, { useState } from "react";
// import axios from "axios";
import List from "./List";
import axios from "axios";

const Curd = () => {
  const [student, setStudent] = useState([]);
  const [status, setStatus] = useState();
  const navigate = useNavigate();

  const textchange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const addItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post("ttp://localhost:4500/data", student);
      // console.log(res, "resruiuyuy");
      setStatus(true);
    } catch (err) {
      console.log(err, "data post time error");
    }
  };
  if (status) {
    return (
      <>
        <Curd />
      </>
    );
  }

  return (
    <>
      <div className="container">
        <div className="row">
          <div className="site-page-header-ghost-wrapper">
            <PageHeader
              ghost={false}
              title="Title"
              extra={[
                <Button key="3">Contact</Button>,
                <Button key="2">Service</Button>,
                <Button key="1" type="primary">
                  Primary
                </Button>,
              ]}
            ></PageHeader>
          </div>
        </div>
        <div className="col-md-12 text-center">
          <button onClick={() => navigate("/", { replace: true })}>Add</button>
        </div>
      </div>

      <div className="Main_div">
        <div className="container">
          <div className="row">

            <List />
          </div>
        </div>
      </div>
    </>
  );
};

export default Curd;
