import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const View = () => {
  const { id } = useParams();
  const [student, setStudent] = useState([]);

  useEffect(() => {
    getAlldata();
  }, []);

  const getAlldata = async () => {
    try {
      const student = await axios.get(`http://localhost:4500/data/${id}`);
      // console.log(student.data);
      setStudent(student.data);
    } catch (err) {
      console.log(err, "data view time error");
    }
  };

  return (
    <>
      <div className="container">
        <div className="col-md-10 offset-3  text-center p-5 d-grid">
          <div className="col-md-6 text-center p-2 d-grid">
            <h2 style={{ backgroundColor: "black", color: "white" }}>
              View Items
            </h2>
          </div>
          <table className="">
            <tr>
              <td>No</td>
              <td>Name</td>
              <td>Email</td>
            </tr>
            <tr>
              <td>{student.id}</td>
              <td>{student.name}</td>
              <td>{student.id}</td>
            </tr>
          </table>

        </div>
      </div>
    </>
  );
};

export default View;
