import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
// import Curd from "./Curd";

const Edit = () => {
  const [student, setStudent] = useState({
    name: "",
    id: "",
  });
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    getAlldata();
    edit();
  }, []);

  const getAlldata = async () => {
    try {
      const student = await axios.get(`http://localhost:3003/data/${id}`);
      // .then((res) => {
      // console.log(student.data);
      setStudent(student.data);
      // });
    } catch (err) {
      console.log(err, "data view time error");
    }
  };
  const changetext = async (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };
  const edit = async (e) => {
    e.preventDefault();
    try {
      await axios
        .put(`http://localhost:3003/data/${id}`, student)
        .then((res) => {
          console.log(res.data);

          navigate("/curd", { replace: true });
        });
    } catch (err) {
      console.log(err, "error in edit time");
    }
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="col-md-8 offset-2  mt-  text-center p-5 d-grid">
          <h3>Edit item</h3>

          <input
            type="text"
            className="p-1 mb-2"
            value={student.name}
            placeholder="Edit"
            onChange={(e) => changetext(e)}
            name="name"
          />

          <input
            type="number"
            value={student.id}
            className="p-1 mb-2"
            placeholder="Edit"
            onChange={(e) => changetext(e)}
            name="id"
          />

          <button className="btn btn-warning" onClick={(e) => edit(e)}>
            Edit
          </button>
          <button
            className="btn btn-warning mt-2"
            onClick={(e) => navigate("/curd", { replace: true })}
          >
            Back
          </button>
        </div>
      </div>
    </>
  );
};

export default Edit;
